SELECT 

FROM cdboo_business_plan a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.plan_name = #{planName}


		AND a.business_id = #{business.id}


		AND a.style = #{style}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cdboo_business_plan a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


