mapper.select:
SELECT 

FROM cdboo_child_channel a

WHERE a.id = #{id}

SELECT 
	max(a.channel_no) AS "channelNo"
FROM cdboo_child_channel a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.channel_type = #{channelType}

SELECT
FROM cdboo_child_channel a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.channel_no LIKE '%'||#{channelNo}||'%' '%'+#{channelNo}+'%' concat('%',#{channelNo},'%')
AND a.channel_name LIKE '%'||#{channelName}||'%' '%'+#{channelName}+'%' concat('%',#{channelName},'%')
AND a.theme_type = #{themeType}
AND a.theme_concrete_type = #{themeConcreteType}
AND a.channel_version LIKE '%'||#{channelVersion}||'%' '%'+#{channelVersion}+'%' concat('%',#{channelVersion},'%')
AND a.music_style LIKE '%'||#{musicStyle}||'%' '%'+#{musicStyle}+'%' concat('%',#{musicStyle},'%')
AND a.site = #{site}
AND a.speed = #{speed}
AND a.voice = #{voice}
AND a.element LIKE '%'||#{element}||'%' '%'+#{element}+'%' concat('%',#{element},'%')
AND a.emotion LIKE '%'||#{emotion}||'%' '%'+#{emotion}+'%' concat('%',#{emotion},'%')
AND a.instrument LIKE '%'||#{instrument}||'%' '%'+#{instrument}+'%' concat('%',#{instrument},'%')
AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
AND a.channel_type = #{channelType}
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC,a.channel_no ASC

SELECT
FROM ( SELECT a.id AS "id",b.user_id AS "userId" FROM cdboo_child_channel a,cdboo_user_channel b
a.del_flag = #{DEL_FLAG_NORMAL} AND b.del_flag = #{DEL_FLAG_NORMAL} AND a.id = b.channel_id
AND b.user_id = #{userId}
AND a.channel_no LIKE '%'||#{channelNo}||'%' '%'+#{channelNo}+'%' concat('%',#{channelNo},'%')
AND a.channel_name LIKE '%'||#{channelName}||'%' '%'+#{channelName}+'%' concat('%',#{channelName},'%')
AND a.theme_type = #{themeType}
AND a.theme_concrete_type = #{themeConcreteType}
AND a.channel_version LIKE '%'||#{channelVersion}||'%' '%'+#{channelVersion}+'%' concat('%',#{channelVersion},'%')
AND a.music_style LIKE '%'||#{musicStyle}||'%' '%'+#{musicStyle}+'%' concat('%',#{musicStyle},'%')
AND a.site = #{site}
AND a.speed = #{speed}
AND a.voice = #{voice}
AND a.element LIKE '%'||#{element}||'%' '%'+#{element}+'%' concat('%',#{element},'%')
AND a.emotion LIKE '%'||#{emotion}||'%' '%'+#{emotion}+'%' concat('%',#{emotion},'%')
AND a.instrument LIKE '%'||#{instrument}||'%' '%'+#{instrument}+'%' concat('%',#{instrument},'%')
AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
AND a.channel_type = #{channelType}
AND a.id not in
${item}
AND a.id in
${item}
group by a.id,b.user_id ) d,cdboo_child_channel c where d.id = c.id

SELECT 

FROM cdboo_child_channel a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC,a.channel_no ASC

mapper.update:
UPDATE cdboo_child_channel SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
