mapper.select:
SELECT 

FROM cdboo_child_channel a

WHERE a.id = #{id}

SELECT 

FROM cdboo_child_channel a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.channel_no = #{channelNo}


		AND a.channel_name = #{channelName}


		AND a.theme_type = #{themeType}


		AND a.theme_concrete_type = #{themeConcreteType}


		AND a.channel_version = #{channelVersion}


		AND a.music_style = #{musicStyle}


		AND a.site = #{site}


		AND a.speed = #{speed}


		AND a.voice = #{voice}


		AND a.element = #{element}


		AND a.emotion = #{emotion}


		AND a.instrument = #{instrument}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.channel_type = #{channelType}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT 

FROM cdboo_child_channel a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE cdboo_child_channel SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
