mapper.select:
SELECT 

FROM cdboo_group_child a

WHERE a.id = #{id}

SELECT 

FROM cdboo_group_child a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.group_channel_id = #{groupChannelId.id}


		AND a.child_channel_id = #{childChannelId.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

From cdboo_group_child a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.group_channel_id = #{groupChannelId.id}


		AND a.child_channel_id = #{childChannelId.id}


GROUP BY a.group_channel_id

SELECT 

FROM cdboo_group_child a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cdboo_group_child SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
