mapper.select:
SELECT 

FROM cdboo_music a

WHERE a.id = #{id}

SELECT
FROM cdboo_music a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.music_no LIKE '%'||#{musicNo}||'%' '%'+#{musicNo}+'%' concat('%',#{musicNo},'%')
AND a.music_owner = #{musicOwner}
AND a.music_name LIKE '%'||#{musicName}||'%' '%'+#{musicName}+'%' concat('%',#{musicName},'%')
AND a.actor LIKE '%'||#{actor}||'%' '%'+#{actor}+'%' concat('%',#{actor},'%')
AND a.special LIKE '%'||#{special}||'%' '%'+#{special}+'%' concat('%',#{special},'%')
AND a.id not in
${item}
AND a.id in
${item}
AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

SELECT
FROM cdboo_owner_music b,cdboo_music a
a.del_flag = #{DEL_FLAG_NORMAL} AND b.music_id = a.id AND b.user_id = #{userId}
AND a.music_no LIKE '%'||#{musicNo}||'%' '%'+#{musicNo}+'%' concat('%',#{musicNo},'%')
AND a.music_owner = #{musicOwner}
AND a.music_name LIKE '%'||#{musicName}||'%' '%'+#{musicName}+'%' concat('%',#{musicName},'%')
AND a.actor LIKE '%'||#{actor}||'%' '%'+#{actor}+'%' concat('%',#{actor},'%')
AND a.special LIKE '%'||#{special}||'%' '%'+#{special}+'%' concat('%',#{special},'%')
AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
AND a.id not in
${item}
AND a.id in
${item}
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

SELECT 

FROM cdboo_music a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE cdboo_music SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
