mapper.select:
SELECT 

FROM cdboo_music a

WHERE a.id = #{id}

		SELECT 

		FROM cdboo_music a


			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.music_no = #{musicNo}


				AND a.music_owner = #{musicOwner}


				AND a.music_name = #{musicName}


				AND a.actor = #{actor}


				AND a.special = #{special}


				AND a.create_date = #{createDate}


				AND a.id not in 

  					${item}  





				ORDER BY ${page.orderBy}


				ORDER BY a.create_date DESC

SELECT 

FROM cdboo_music a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE cdboo_music SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
