mapper.select:
SELECT 

FROM cdboo_music_insert a

WHERE a.id = #{id}

SELECT 

FROM cdboo_music_insert a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.insert_no = #{insertNo}


		AND a.insert_name = #{insertName}


		AND a.user_id = #{user.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cdboo_music_insert a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cdboo_music_insert SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
