mapper.select:
SELECT 

FROM cdboo_timestep a

WHERE a.id = #{id}

SELECT 

FROM cdboo_timestep a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.timestep_no LIKE concat('%',#{timestepNo},'%')


		AND a.timestep_name LIKE concat('%',#{timestepName},'%')


		AND a.starttime BETWEEN #{beginStarttime} AND #{endStarttime}


		AND a.endtime BETWEEN #{beginEndtime} AND #{endEndtime}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT 

FROM cdboo_timestep a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE cdboo_timestep SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
