mapper.select:
SELECT 

FROM cdboo_user_channel a

WHERE a.id = #{id}

SELECT 

FROM cdboo_user_channel a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{user.id}


		AND a.channel_id = #{channel.id}


		AND a.music_id = #{music.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT 
	a.user_id AS "user.id",a.channel_id AS "channel.id",COUNT(a.music_id) AS "musicSize"
FROM cdboo_user_channel a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{user.id}


		AND a.channel_id = #{channel.id}


		AND a.music_id = #{music.id}


group by a.user_id,a.channel_id

SELECT a.channel_id AS "channel.id"
FROM cdboo_user_channel a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{user.id}


group by a.channel_id

SELECT 

FROM cdboo_user_channel a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE cdboo_user_channel SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
