mapper.select:
SELECT

FROM cdboo_user_group_channel a

WHERE a.id = #{id}

SELECT

FROM cdboo_user_group_channel a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.user_id = #{user.id}


        AND a.group_channel_id = #{cdbooChannel.id}





        ORDER BY ${page.orderBy}


        ORDER BY a.create_date DESC

SELECT
a.user_id AS "user.id", a.group_channel_id AS "cdbooChannel.id"
FROM cdboo_user_group_channel a

    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.user_id = #{user.id}


        AND a.group_channel_id = #{cdbooChannel.id}



group by a.user_id,a.group_channel_id

SELECT a.group_channel_id AS "cdbooChannel.id"
FROM cdboo_user_group_channel a

    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.user_id = #{user.id}


group by a.group_channel_id

SELECT

FROM cdboo_user_group_channel a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.create_date DESC

mapper.update:
UPDATE cdboo_user_group_channel SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
