mapper.select:
SELECT 

FROM cdboo_plan a

WHERE a.id = #{id}

SELECT 

FROM cdboo_plan a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.plan_no = #{planNo}


		AND a.play_name = #{playName}


		AND a.user_id = #{user.id}


		AND a.user_timestep_id = #{userTimestepId}


		AND a.user_channel_id = #{userChannelId}


		AND a.music_style = #{musicStyle}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cdboo_plan a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cdboo_plan SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
